        &quot;avg_latency&quot;:   n.getAverageLatency(),
        &quot;success_rate&quot;:  n.getSuccessRate(),
    }
}

66.text:
Handle asynchronous operations:
type AsyncNode struct {
    *flyt.BaseNode
    workers int
    queue   chan Task
    wg      sync.WaitGroup
}

func NewAsyncNode(workers int) *AsyncNode {
    n := &amp;AsyncNode{
        BaseNode: flyt.NewBaseNode(),
        workers:  workers,
        queue:    make(chan Task, workers*2),
    }

    // Start workers
    for i := 0; i &lt; workers; i++ {
        go n.worker()
    }

    return n
}

func (n *AsyncNode) worker() {
    for task := range n.queue {
        n.processTask(task)
        n.wg.Done()
    }
}

func (n *AsyncNode) Exec(ctx context.Context, prepResult any) (any, error) {
    tasks := prepResult.([]Task)
    results := make([]Result, len(tasks))

    // Submit all tasks
    for i, task := range tasks {
        n.wg.Add(1)
        task.Index = i
        task.Results = &amp;results
        n.queue &lt;- task
    }

    // Wait for completion
    done := make(chan struct{})
    go func() {
        n.wg.Wait()
        close(done)
    }()

    select {
    case &lt;-ctx.Done():
        return nil, ctx.Err()
    case &lt;-done:
        return results, nil
    }
}

func (n *AsyncNode) Close() {
    close(n.queue)
}

