func ToSlice(v any) []any {
    if v == nil {
        return []any{}
    }

    rv := reflect.ValueOf(v)
    if rv.Kind() == reflect.Slice {
        result := make([]any, rv.Len())
        for i := 0; i &lt; rv.Len(); i++ {
            result[i] = rv.Index(i).Interface()
        }
        return result
    }

    // Single item
    return []any{v}
}

