func ChunkSlice[T any](slice []T, chunkSize int) [][]T {
    var chunks [][]T

    for i := 0; i &lt; len(slice); i += chunkSize {
        end := i + chunkSize
        if end &gt; len(slice) {
            end = len(slice)
        }
        chunks = append(chunks, slice[i:end])
    }

    return chunks
}

