func Debounce(fn func(), delay time.Duration) func() {
    var timer *time.Timer
    var mu sync.Mutex

    return func() {
        mu.Lock()
        defer mu.Unlock()

        if timer != nil {
            timer.Stop()
        }

        timer = time.AfterFunc(delay, fn)
    }
}

// Usage
saveDebounced := Debounce(func() {
    saveToDatabase()
}, 5*time.Second)

// Call multiple times, only last one executes
saveDebounced()
saveDebounced()
saveDebounced()

