func statePost(ctx context.Context, shared *flyt.SharedStore, prepResult, execResult any) (flyt.Action, error) {
    currentState := shared.GetString(&quot;state&quot;)

    switch currentState {
    case &quot;init&quot;:
        shared.Set(&quot;state&quot;, &quot;processing&quot;)
        return &quot;process&quot;, nil
    case &quot;processing&quot;:
        shared.Set(&quot;state&quot;, &quot;complete&quot;)
        return &quot;finalize&quot;, nil
