Maintain state across executions:
func createCounterNode() flyt.Node {
    count := 0
    mu := &amp;sync.Mutex{}

    return flyt.NewNode(
        flyt.WithExecFunc(func(ctx context.Context, prepResult any) (any, error) {
            mu.Lock()
            count++
            current := count
            mu.Unlock()

            return fmt.Sprintf(&quot;Execution #%d&quot;, current), nil
        }),
    )
}

